window.onload = function () {
    var hds = [];
    var x = 500;
    var y = 200;
    var lastTime = 0;
    var interval = 500;
    var time = 3000;
    var time2 = 31;
    var score = 0;
    var hero = new hero(450, 450, 80, 133, h);

    function hero(x, y, width, height, img) {
        dropObjects.call(this, x, y, width, height, img);
        this.x = 450;
        this.y = 450;
    }

    function dropObjects(x, y, width, height, img) {
        this.x = Math.random() * (1000 - 30);
        this.y = -height;
        this.width = width;
        this.height = height;
        this.img = img;

        this.paint = function (ctx) {
            ctx.drawImage(this.img, this.x, this.y);

        }

        this.step = function () {
            this.y++;
        }

        this.hit = function (component) {
            var c = component;
            return c.x > this.x - c.width && c.x  this.y - c.height && c.y = 600;
        }
    }

    function checkHit() {
        for (var i = 0; i = interval;
    }

    var ctrl = setInterval(function () {
        ctx.drawImage(bg, 0, 0);
        component();
        paintComponent();
        stepComponent();
        checkHit();
        deleteComponent();
        hero.paint(ctx);
        ctx.drawImage(tip, 800, 5);
        ctx.drawImage(time_tip, 25, 25);
        writeDown(score, 888, 63, 878, 63, "30px 微软雅黑 ", "white");
        resetTime();

    }, 10)

    document.onkeydown = function (ev) {
        if (time2 >= 1) {
            if (ev.keyCode == 37) {
            hero.x -= 10;
            hero.paint(ctx);
            }
        if (ev.keyCode == 39) {
            hero.x += 10;
            hero.paint(ctx);
            }
        }

    }
}

